package apple

import (
	"testing"

	"github.com/go-pay/xlog"
)

func TestLookUpOrderId(t *testing.T) {
	orderId := "2000000184445477"
	rsp, err := client.LookUpOrderId(ctx, orderId)
	if err != nil {
		if statusErr, ok := IsStatusCodeError(err); ok {
			xlog.Errorf("%+v", statusErr)
			// do something
			return
		}
		xlog.Errorf("client.LookUpOrderId(),err:%+v", err)
		return
	}
	/**
		{
	    "status":0,
	    "signedTransactions":[
	        "..HCDFGxo5p1xkl9MWNymeC-K_EMx1P1_3cGsz0-HCybHUvMHRkipie33YZesLZy33PqPL1Nwbb9pkFAtmbvSETQ"
	    ]
	}
	*/
	if len(rsp.SignedTransactions) == 0 {
		xlog.Debugf("rsp:%+v", rsp)
	}
	for _, v := range rsp.SignedTransactions {
		transaction, _ := v.DecodeSignedTransaction()
		xlog.Debugf("transactions:%+v", transaction)
	}
}
