package alipay

import (
	"github.com/go-pay/gopay"
	"github.com/go-pay/gopay/alipay"
	"github.com/go-pay/gopay/pkg/xlog"
)

func SystemOauthToken() {
	privateKey := ""
	//初始化支付宝客户端
	//    appId：应用ID
	//    privateKey：应用秘钥
	//    isProd：是否是正式环境
	client, err := alipay.NewClient("2016091200494382", privateKey, false)
	if err != nil {
		xlog.Error(err)
		return
	}
	//配置公共参数
	client.SetCharset("utf-8").
		SetSignType(alipay.RSA2).
		SetNotifyUrl("https://www.fmm.ink")

	//请求参数
	bm := make(gopay.BodyMap)
	bm.Set("grant_type", "authorization_code")
	bm.Set("code", "3a06216ac8f84b8c93507bb9774bWX11")

	//发起请求
	aliRsp, err := client.SystemOauthToken(ctx, bm)
	if err != nil {
		xlog.Error("err:", err)
		return
	}
	xlog.Debug("aliRsp:", *aliRsp)
	xlog.Debug("aliRsp:", aliRsp.Response.AccessToken)
	xlog.Debug("aliRsp:", aliRsp.SignData)

	//支付宝小程序创建订单
	bm2 := make(gopay.BodyMap)
	bm2.Set("subject", "创建订单")
	bm2.Set("buyer_id", aliRsp.Response.UserId)
	bm2.Set("out_trade_no", "GZ201901301040355708")
	bm2.Set("total_amount", "0.01")

	rsp, err := client.TradeCreate(ctx, bm2)
	if err != nil {
		xlog.Error("err:", err)
		return
	}
	xlog.Debug("rsp:", *rsp)
	xlog.Debug("TradeNo:", rsp.Response.TradeNo)
}
