package alipay

import (
	"github.com/go-pay/gopay"
	"github.com/go-pay/gopay/alipay"
	"github.com/go-pay/gopay/pkg/xlog"
)

func TradeFastPayRefundQuery() {
	//aliPayPublicKey := ""
	privateKey := ""
	//初始化支付宝客户端
	//    appId：应用ID
	//    privateKey：应用私钥，支持PKCS1和PKCS8
	//    isProd：是否是正式环境
	client, err := alipay.NewClient("2016091200494382", privateKey, false)
	if err != nil {
		xlog.Error(err)
		return
	}
	//配置公共参数
	client.SetCharset("utf-8").
		SetSignType(alipay.RSA2)

	//请求参数
	bm := make(gopay.BodyMap)
	bm.Set("out_trade_no", "GZ201907301420334577")
	bm.Set("out_request_no", "GZ201907301420334577")
	//发起退款查询请求
	aliRsp, err := client.TradeFastPayRefundQuery(ctx, bm)
	if err != nil {
		xlog.Error("err:", err)
		return
	}
	xlog.Debug("aliRsp:", *aliRsp)
}
